import React, { Component } from "react";
import Checkbox from "../Checkbox/Checkbox";
import { Button } from 'react-bootstrap';
import {withRouter} from 'react-router-dom';



const OPTIONS = ["One", "Two", "Three"];

class Consent extends Component {
  constructor(props) {
    super(props);

    const prolific_id = this.props.location.state.prolific_id
    const study_id    = this.props.location.state.study_id
    const longit_id   = this.props.location.state.longit_id

  this.state = {
    checkboxes: OPTIONS.reduce(
      (options, option) => ({ // to change options and options for name, label for clarity 
        ...options,
        [option]: false
      }),
      {}
    ),
    prolific_id: prolific_id, 
    study_id : study_id,
    longit_id: longit_id,
    verified: false, 
    count: 0

  };

  console.log(this.state.checkboxes)

  this.redirectToTarget.bind(this);
  this.handleFormSubmit.bind(this);

}


  handleCheckboxChange = changeEvent => {
    const { name } = changeEvent.target;

    console.log(name)
    console.log(this.state.count)

    Object.keys(this.state.checkboxes)
      .filter(checkbox => this.state.checkboxes[checkbox])
      .forEach(checkbox => {
        console.log(checkbox, "is selected.");
      });

    this.setState(prevState => ({
      checkboxes: {
        ...prevState.checkboxes,
        [name]: !prevState.checkboxes[name],
      }
    }));

    if (this.state.count===3){
        this.setState({
          verified: true
        });
      }
  };

redirectToTarget = () => {
  this.props.history.push({
    pathname: `/Survey`, 
    state: {participant_info: this.state} 
  })
}

handleFormSubmit = formSubmitEvent => {
    formSubmitEvent.preventDefault();

    Object.keys(this.state.checkboxes)
      .filter(checkbox => this.state.checkboxes[checkbox])
      .forEach(checkbox => {
        console.log(checkbox, "is selected.");
      });
  };


createCheckbox = option => (
    <Checkbox
      label={option} // to be changed for clarity 
      isSelected={this.state.checkboxes[option]}
      onCheckboxChange={this.handleCheckboxChange}
      key={option} // to be changed for clarity 
    />
  );

  createCheckboxes = () => OPTIONS.map(this.createCheckbox);


  render() {
    return (
      <div className="container">
      <form onSubmit={this.handleFormSubmit}>
          {this.createCheckboxes()}

          <div className="form-group mt-2">
              <button
                type="button"
                className="btn btn-outline-primary mr-2"
                disabled={!this.state.verified}
                onClick={this.redirectToTarget}>
                Submit
              </button>
          </div>
        </form>
      </div>
    );
  }
}

/*
<form onSubmit={this.handleFormSubmit}>
              {this.createCheckboxes()}
            </form>


*/

export default withRouter(Consent);
