import React, { useState } from 'react';
import 'react-bootstrap-range-slider/dist/react-bootstrap-range-slider.css';
import RangeSlider from 'react-bootstrap-range-slider';

import './ConfRate.css';

// we can use hooks instead of creating a class with new React JS 
const ConfRate = () => {

// generate a random number between 50 and 100 
const init_value = Math.round(Math.random()*50+50)


const [ value, setValue ] = useState(init_value); // initial position of the slide: should be a random value between 50 and 100  

    return ( 
    <div className='slider'>
      <p>How confident are you?</p>
      <span className='slider-val'>Very uncertain</span>
      <span className='slider-val'>Very certain</span> 
      <br></br>    
      <RangeSlider 
        value            = {value}
        min              = {50}
        max              = {100}
        step             = {1}
        disabled         = {false} 
        size             = {'lg'}
        variant          = {'dark'}
        tooltip          = {'auto'}
        tooltipPlacement = {'top'}
        onChange={changeEvent => setValue(changeEvent.target.value)}
      />
      </div>

    );
  }


export default ConfRate;
