import React, { useState } from 'react';
import 'react-bootstrap-range-slider/dist/react-bootstrap-range-slider.css';
import RangeSlider from 'react-bootstrap-range-slider';

import './Brick.css';

// we can use hooks instead of creating a class with new React JS 
const Brick = () => {

  const [ value, setValue ] = useState(0); 

  // const animation_property = this.props.animation ? 'activate' : 'reset'; // this should be adjusted 
  // const selected           = (this.props.noFeedback==='') ? 'selected' : 'notSelected'
  // const selected           = (this.props.symbolHighlight ==='') ? 'selected' : 'notSelected'

    return ( 
    <div>     
      <RangeSlider 
        value   ={value}
        onChange={changeEvent => setValue(changeEvent.target.value)}
      />
      </div>

    );
  }


export default Brick;

