import React from 'react';
import PropTypes from 'prop-types';
import { CSSTransitionGroup } from 'react-transition-group';
import Question from '../QuizQuestion/Question';
import QuestionCount from '../QuizQuestion/QuestionCount';
import AnswerOption from '../QuizQuestion/AnswerOption';


// import './CheckboxQuiz.css';
// Maybe import for consistency CHANGE 
const Checkbox = ({ label, isSelected, onCheckboxChange }) => (
  <div className="form-check">
    <label>
      <input
        type="checkbox"
        name={label}
        checked={isSelected}
        onChange={onCheckboxChange}
        className="form-check-input"
      />
      {label}
    </label>
  </div>
);


/*const Checkbox = ({type = 'checkbox', name, checked = false, onChange, className }) => (
  <input type={type} name={name} checked={checked} onChange={onChange} />
);


Checkbox.propTypes = {
  type: PropTypes.string,
  name: PropTypes.string.isRequired,
  checked: PropTypes.bool,
  onChange: PropTypes.func.isRequired,
  className:PropTypes.string
}
*/

class CheckboxQuiz extends React.Component {

  constructor(props){
    super(props);


  var options = {}

  // Object.keys(this.props.answerOptions).map(function(key, index) {
  //   // options[key] = false; // mybe to change to type
  //   options[key.type] = false; // mybe to change to type 


  // });

for (var key in this.props.answerOptions) {

  options[this.props.answerOptions[key].type] = false // this.props.answerOptions[key].type
}

console.log(options);

 this.state = {
    report: '',
    answer: '',
    answercheck: false,
    shouldBlockNavigation: false,
    checkboxes: options, 
  }


  console.log('CheckboxQuiz initial state:',this.state) // initial states 

  this.handleSubmit       = this.handleSubmit.bind(this);  
  this.handleChangeReport = this.handleChangeReport.bind(this);
  this._handleRefresh     = this._handleRefresh.bind(this); 
  this.createCheckBox     = this.createCheckBox.bind(this); 
} // constructor


componentDidMount() {
  this._isMounted = true;
  document.getElementById("create-course-form").reset();
  document.body.style.background= '#fff';   
  this.setState({
    report: '',
    answer: '',
    answercheck: false,
    shouldBlockNavigation: false}
    )
  window.history.pushState(window.state, null, window.location.href)
  window.onbeforeunload = null;
  window.addEventListener("keypress", e => this._handleRefresh(e));

}

_handleGoBack(event){
    window.history.go(1);
  }

handleChangeReport(event) {

  var test = Number(event.target.value)

  this.setState({
    report: event.target.value,
    answercheck: false 
    }
    );

  // console.log(event.key)
  if ((test!=="") && (test!==null) && (test>parseInt(this.props.constraint[0].min)) && (test<parseInt(this.props.constraint[1].max))) 
  {
    this.setState({
    answercheck: true}
    );
    // console.log(this.state.answercheck)
  }
  else {
    event.preventDefault()
  }

}

_handleRefresh(e){
  if (e.key==='Enter') {
  // console.log('enter e', e.srcElement);

  // console.log('value', e.target.value);

  var test = this.state.report
  // console.log('test',this.state.report)

  if ((test!=="") && (test!==null) && (test>parseInt(this.props.constraint[0].min)) && (test<parseInt(this.props.constraint[1].max))) 
  {
    this.setState({
    answercheck: true}
    );

    // console.log(this.state.answercheck)
    // Send answers to the parent component
    document.getElementById("create-course-form").reset();
    let prev_report = this.state.report 
        this.setState({
          report: ''}
        )

        this.props.onAnswerSelected(this.state.report,this.props.questionId,e) 
  }
  else {
    e.preventDefault()
  }

  }
}

  handleSubmit(event) {

        // console.log(event)

        event.preventDefault();

        // Check if any of the boxes are checked -> combine the final report and send it to the parent Quiz Block component 

        let prev_report = this.state.report 
        this.setState({
          report: ''}
        )

        this.props.onAnswerSelected(this.state.report,this.props.questionId,event)  
}


handleCheckboxChange(changeEvent,e) {
  console.log('ChangeEvent',changeEvent)

  this.setState(prevState => ({
      checkboxes: {
        ...prevState.checkboxes,
        [changeEvent]: !prevState.checkboxes[changeEvent],
      }
    }));
}


createCheckBox(key) {
  console.log(key.type)
    return (
      <Checkbox
        type            ={key.type}
        label           ={key.content}
        isSelected      ={this.state.checkboxes[key]}
        // answerType      ={key.type}
        // questionId      ={props.questionId}
        onCheckboxChange={(e)=>this.handleCheckboxChange(key.type,e)}
      />
    );
  }


render() {

  return (
    <CSSTransitionGroup
      className="container"
      component="div"
      transitionName="fade"
      transitionEnterTimeout={800}
      transitionLeaveTimeout={500}
      transitionAppear
      transitionAppearTimeout={500}
    >
      <div key={this.props.questionId}>
        <QuestionCount counter={this.props.questionCount} total={this.props.questionTotal} />
        <Question content={this.props.question}/>

        <ul className="answerOptions">
          {this.props.answerOptions.map(this.createCheckBox)}
        </ul>

      </div>
      <p></p>
      <div className="col-md-12 pad-20">
        <div>
          <button type="button" className="btn btn-save btn-primary pad-20" disabled={!this.state.answercheck} onClick={this.handleSubmit}>Submit
            </button>
          </div>
      </div>

    </CSSTransitionGroup>
  );
}

} // closing class 



CheckboxQuiz.propTypes = {
  answer: PropTypes.string.isRequired,
  answerOptions: PropTypes.array.isRequired,
  question: PropTypes.string.isRequired,
  questionId: PropTypes.number.isRequired,
  questionCount: PropTypes.number.isRequired,
  questionTotal: PropTypes.number.isRequired,
  onAnswerSelected: PropTypes.func.isRequired,
};

export default CheckboxQuiz;
