import React, { Component } from 'react'
import { Slider, Rail, Handles, Tracks, Ticks } from 'react-compound-slider'
import { Handle, Track, TooltipRail, Tick } from './components' // example render components - source below
import PropTypes from 'prop-types'

import './ConfRateNew.css'

const sliderStyle = {
  position: 'relative',
  width: '100%',
}

const domain        = [50, 100]
const defaultValues = [75] // to be changed 


class ConfRate extends Component {
  state = {
    values: defaultValues.slice(),
    update: defaultValues.slice(),
  }

  onUpdate = update => {
    this.setState({ update })
  }

  onChange = values => {
    this.setState({ values })
  }

  handleMouseUp = event => {
      console.log('Mouse event up!', event)

    }

  render() {
    const {
      state: { values, update },
    } = this

    console.log('update',this.state.update)
    console.log('value',this.state.value)



    return (
      <div>
      <div className='sliderText'>How confident are you?</div>
      <span className='slider-val'>Very uncertain</span>
      <span className='slider-val'>Very certain</span>


      <div style={{ height: 120, width: '50%', marginTop:30}}>
        <Slider
          mode     ={1}
          step     ={1}
          domain   ={domain}
          rootStyle={sliderStyle}
          onUpdate ={this.onUpdate}
          onChange ={this.onChange}
          values   ={values}
        >
          <Rail>{railProps => <TooltipRail {...railProps} />}</Rail>
          <Handles>
            {({ handles, activeHandleID, getHandleProps }) => (
              <div className="slider-handles">
                {handles.map(handle => (
                  <Handle
                    key           ={handle.id}
                    handle        ={handle}
                    domain        ={domain}
                    isActive      ={handle.id === activeHandleID}
                    getHandleProps={getHandleProps}
                    handleMouseUp = {this.props.confClicked(this.state.value)} // a callback 
                  />
                ))}
              </div>
            )}

          </Handles>
          <Tracks right={false}>
            {({ tracks, getTrackProps }) => (
              <div className="slider-tracks">
                {tracks.map(({ id, source, target }) => (
                  <Track
                    key={id}
                    source={source}
                    target={target}
                    getTrackProps={getTrackProps}
                  />
                ))}
              </div>
            )}
          </Tracks>
          <Ticks count={5 /* generate approximately 15 ticks within the domain */}>
            {({ ticks }) => (
          <div className="slider-ticks">
            {ticks.map(tick => (
              <Tick key={tick.id} tick={tick} count={ticks.length} />
          ))}
          </div>
      )}
    </Ticks>
        </Slider>
      </div>
      </div>
    )
  }
}

export default ConfRate


ConfRate.propTypes = {
    confClicked: PropTypes.function.isRequired
  }
