import React from 'react';
import { Button } from 'react-bootstrap';
import {withRouter} from 'react-router-dom';
import PropTypes from 'prop-types';
import queryString from 'query-string';

import Checkbox from '../Checkbox/Checkbox';

// import './Consent.css';
// import './bootstrap.min.css';


// Fetch participant data onClick after veryfing that all the boxes are ticked 
const checkboxes = [
  {
    name: 'check-box-1',
    key: 'checkBox1',
    label: 'I have read the information above, and understand what the study involves',
  },
  {
    name: 'check-box-2',
    key: 'checkBox2',
    label: 'I understand that my anonymised personal data can be shared with others for future research, shared in public databases and in scientific reports.',
  },

  {
    name: 'check-box-3',
    key: 'checkBox3',
    label: 'I understand that I am free to withdraw from this study at any time without giving a reason and this will not affect my future medical care or legal rights.',
  },
  {
    name: 'check-box-4',
    key: 'checkBox4',
    label: 'I understand the potential benefits and risks of participating, the support available to me should I become distressed during the research, and who to contact if I wish to lodge a complaint.',
  },
  {
    name: 'check-box-5',
    key: 'checkBox5',
    label: 'I understand the inclusion and exclusion criteria in the Information Sheet and as explained to me by the researcher.  I confirm that I do not fall under the exclusion criteria.',
},
  {
    name: 'check-box-6',
    key: 'checkBox6',
    label: 'I agree that the research project named above has been explained to me to my satisfaction and I agree to take part in this study.',
  },
];



class Consent extends React.Component {
  constructor(props) {
    super(props);

    const prolific_id = this.props.location.state.prolific_id
    const study_id    = this.props.location.state.study_id
    const longit_id   = this.props.location.state.longit_id


      this.state = {
      checked: false,
      prolific_id: prolific_id, 
      study_id : study_id,
      longit_id: longit_id
    }

    this.redirectToTarget.bind(this);
  }

  static contextTypes = {
    router: PropTypes.object
  }


  redirectToTarget = () => {

  this.props.history.push({
    pathname: `/Survey`, 
    state: {participant_info: this.state} 
  })
}


  handleCheckboxChange = event =>
    this.setState({checked: event.target.checked})

render() {
  return (
      <div>
        <div>
          <label>
            <Checkbox
              checked={this.state.checked}
              onChange={this.handleCheckboxChange}
            />
            <span></span>
          </label>
        </div>
      <div className="buttonContainer">
          <Button type="button" disabled={!this.state.checked} onClick={()=>this.redirectToTarget()}>Next</Button>
      </div>
    </div>  
    );
  }
}

export default withRouter(Consent);
